$maxsize_b = return_bytes(@ini_get('upload_max_filesize'));
if ($maxsize_b AND $maxsize_b > 0) {
	$maxsize_m = floor($maxsize_b / 1024 / 1024);

	if ($value > $maxsize_m) {
		$error = "Maximum filesize allowed by your server is $maxsize_m MB";
	}
}

