if (!ctype_digit($value)) {
	$error = 'Please input numbers only';
} elseif ($value < 1) {
	$error = 'Please input a number >= 1';
}

$emails = array_remove_empty(explode(',', $value));
$value = array();

if ($emails) {
	foreach ($emails as $email) {
		$email = trim($email);
		$value[] = $email;

		if (!$email) {
			continue;
		}

		if (!is_email($email)) {
			$error = 'The email(s) you entered are invalid.';
			break;
		}
	}
}

